QUnit.test("local files",function(c){var d=document.querySelector("#locals");var b=c.async(6);var a=new imagesLoaded(d,function(e){c.ok(true,"callback function triggered");c.equal(a,e,"callback argument and instance match");b()});a.on("fail",function(){c.ok(true,"fail event triggered");b()});a.on("always",function(){c.ok(true,"always event triggered");b()});a.on("progress",function(e,f){c.ok(true,"image progressed");if(f.img.src.indexOf("img/not-there.jpg")!==-1){c.ok(!f.isLoaded,"thunder cloud is not loaded")}else{c.ok(f.isLoaded,"image is loaded")}b()})});