$.validator.addMethod("bankaccountNL",function(f,c){if(this.optional(c)){return true}if(!(/^[0-9]{9}|([0-9]{2} ){3}[0-9]{3}$/.test(f))){return false}var e=f.replace(/ /g,""),d=0,a=e.length,h,b,g;for(h=0;h<a;h++){b=a-h;g=e.substring(h,h+1);d=d+b*g}return d%11===0},"Please specify a valid bank account number");