$.validator.addMethod("cpfBR",function(f){f=f.replace(/([~!@#$%^&*()_+=`{}\[\]\-|\\:;'<>,.\/? ])+/g,"");if(f.length!==11){return false}var e=0,d,c,b,a;d=parseInt(f.substring(9,10),10);c=parseInt(f.substring(10,11),10);b=function(h,i){var g=(h*10)%11;if((g===10)||(g===11)){g=0}return(g===i)};if(f===""||f==="00000000000"||f==="11111111111"||f==="22222222222"||f==="33333333333"||f==="44444444444"||f==="55555555555"||f==="66666666666"||f==="77777777777"||f==="88888888888"||f==="99999999999"){return false}for(a=1;a<=9;a++){e=e+parseInt(f.substring(a-1,a),10)*(11-a)}if(b(e,d)){e=0;for(a=1;a<=10;a++){e=e+parseInt(f.substring(a-1,a),10)*(12-a)}return b(e,c)}return false},"Please specify a valid CPF number");